import React from 'react';
import { Link, useLocation} from 'react-router-dom';

function Header({ onLogout, onLogin, isLoggedIn, onRegister }) {

  const { pathname } = useLocation()

  return (
    <header className="header">
        <a className="header__logo" href="#"></a>
        {pathname==='/sign-up' && (<Link to="/sign-in" className="header__link">Войти</Link>)}
        {pathname==='/sign-in' && (<Link to="/sign-up" className="header__link">Зарегистрироваться</Link>)}
        {pathname==='/' && (<Link to="/sign-in" className="header__link">Выйтffff111111ffи</Link>) && (<Link to="/sign-in" className="header__link">Выйти</Link>)}
    </header>  );
}

export default Header;
