import React from 'react';
import { Link, useLocation} from 'react-router-dom';

function Header({ onLogout, onLogin, isLoggedIn, onRegister }) {

  const { pathname } = useLocation()

  return (
    <header className="header">
        <a className="header__logo" href="#"></a>
        {pathname==='/sign-up' && (<button className="header__title"><Link>Войти</Link></button>)}
        {pathname==='/sign-in' && (<button className="header__title"><Link>Войти</Link>Зарегистрироваться</button>)}
    </header>  );
}

export default Header;
