import React from 'react';
import '../index.css';
import PopupWithForm from './PopupWithForm';

function AddPlacePopup({ isOpen, onClose, onAddPlace }) {

    const linkInputRef = React.useRef();

    function handleAddPlaceSubmit(e) {
        e.preventDefault();
        onAddPlace({
            avatar: linkInputRef.current.value,
        });
    } 

    return (
        <PopupWithForm
            name='add'
            isOpen={isOpen}
            onClose = {onClose}
            popupTitle='Новое место'
            buttonTitle='Сохранить'>
            <div className="field-container">
            <input id="place-input" className="popup__input" name="name" type="text" placeholder="Название" required minLength="2" maxLength="30" />
            <span className="place-input-error" id="place-error"></span>
            </div>
            <div className="field-container">
            <input id="link-input" className="popup__input" name="link" type="url" placeholder="Ссылка на картинку" required />
            <span className="link-input-error" id="link-error"></span>
            </div>
      </PopupWithForm>

    )
}

export default AddPlacePopup
