import React from 'react';
import { useState } from 'react';
import '../index.css';
import PopupWithForm from './PopupWithForm';
import { CurrenUserContext } from '../contexts/CurrentUserContext';


function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar }) {

    const linkRef = React.useRef();

    function handleSubmit(e) {
        e.preventDefault();
        onUpdateAvatar({
            avatar: linkRef.current/* Значение инпута, полученное с помощью рефа */,
        });
    } 

    const currentUser = React.useContext(CurrenUserContext);
    
    const [userName, setUserName] = useState('') 
    const [userDescription, setUserDescription] = useState('') 

    React.useEffect(() => {
        setUserName(currentUser.name);
        setUserDescription(currentUser.about);
      }, [currentUser]); 

    function handleChangeUserName(e) {
        setUserName(e.target.value)
    }

    function handleChangeUserDescription(e) {
        setUserDescription(e.target.value)
    }

    return (
        <PopupWithForm
            name='avatar'
            isOpen={isOpen}
            onClose = {onClose}
            onSubmit={handleSubmit}
            popupTitle='Обновить аватар'
            buttonTitle='Сохранить'>
            <div className="field-container">
            <input ref={linkRef} id="avatar-input" className="popup__input" name="avatar" type="url" placeholder="Ссылка на картинку" required />
            <span className="avatar-input-error" id="avatar-error"></span>
            </div>
      </PopupWithForm>

    )
}

export default EditAvatarPopup
