import React, { useState } from 'react';
import { Link } from 'react-router-dom';



function Register({ onRegister }) {
    const [registerData, setRegisterData] = useState ({
        email: '',
        password: '',
        confirmPassword: '',
    })

    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRegisterData({
            ...registerData,
            [name]: value,
        })
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        const { confirmPassword, ...data } = registerData;
        if (confirmPassword !== data.password){
            return setMessage ('Пароли должны совпадать')
        }
        onRegister(registerData)
            .catch(err => setMessage(err.message || 'Что-то пошло не так'))
    }

  
    return (
    <article>
        <div className="login__container">
        <form onSumbit={handleSubmit} className="login__form" noValidate>
            <h3 className="login__title">Регистрация</h3>
            <div className="field-container">
            <input onChange={handleChange} className="login__input" name="email" type="email" placeholder="Email" required minLength="2" maxLength="30" />
            </div>
            <div className="field-container">
            <input onChange={handleChange} className="login__input" name="password" type="password" placeholder="Пароль" required />
            </div>
            <button className="login__save" type="submit">Зарегистрироваться</button>
            <p className="login__text">Уже зарегистрированы? <Link to="/sign-in" className="login__link">Войти</Link></p>
        </form>
        </div>
    </article>
)}

export default Register;
