import { useState, useEffect } from 'react';
import '../index.css';
import '../components/App.js'
import editButton from '../images/edit-button.svg';
import addButton from '../images/add-button.svg';
import avatar from '../images/avatar.png';
import {api} from '../utils/Api.js';
import Card from '../components/Card.js'

function Main({onEditAvatar, onEditProfile, onAddPlace, onCardClick}) {

  const [userAvatar, getUserAvatar] = useState([])
  const [userName, getUserName] = useState([])
  const [userDescription, getUserDescription] = useState([])
  const [cards, getCards] = useState([])

  function requestData() {
    api.getAllNeededData()
      .then((argument)=> {
        const [ profileInfo, initialCards ] = argument;
        getUserAvatar(profileInfo.avatar);
        getUserName(profileInfo.name);
        getUserDescription(profileInfo.about);
        getCards(initialCards);
      })
      .catch((err) => {
        console.log(err);
      })      
  }

  useEffect(() => {
    requestData()
  }, []) 

  return (
    <main className="content">
    <section className="profile">
      <div className="profile__container">
        <img src={userAvatar} alt="аватар профиля" className="profile__avatar"
            onClick={onEditAvatar}
        ></img>
        <button className="profile__avatar-edit-button" type="button">
          <img className="profile__avatar-edit" src={editButton} alt="редактировать аватар"></img>
        </button>

        <div className="profile__info">
          <div className="profile__info-container">
            <h1 className="profile__name">{userName}</h1>
            <button className="profile__edit-button" type="button"
            onClick={onEditProfile}
            >
              <img className="profile__edit-button-image" src={editButton} alt="редактировать профиль"></img>
            </button>
          </div>
          <p className="profile__bio">{userDescription}</p>
        </div>
      </div>
      <button className="profile__add-button" type="button"
            onClick={onAddPlace}
            >
        <img className="profile__add-button-image" src={addButton} alt="иконка добавить новую картинку"></img>
      </button>
    </section>

    <section className="elements">
      { cards.map((card) => {
        return (
            <Card
            key = {card._id}
            card={card}
            onCardClick={onCardClick}
            />
      )
    }     )  
  }
    </section>
    
    </main>  );
}

export default Main;
