import '../index.css';
import closeButton from '../images/close-button.svg';


function PopupWithForm({name, popupTitle, buttonTitle, children, isOpen}) {
  
    return (
    <>
    <article 
        className={`popup popup_${name} ${isOpen ? "popup_opened" : ""}`}>
        <div className="popup__container">
        <button className="popup__close-button" type="button">
            onClick = {onEditAvatarClose}
            <img className="popup__image-close" src={closeButton} alt="закрыть попап"></img>
        </button>
        <form className="popup__form popup__form_profile" name={`${name}`} noValidate>
            <h3 className="popup__title">{popupTitle}</h3>
            {children}

            <button className="popup__save" type="submit">{buttonTitle}</button>
        </form>
        </div>
    </article>
    </>
)}

export default PopupWithForm;
