import React from 'react';
import { Link, useLocation} from 'react-router-dom';

function Header({ onLogout, onLogin, isLoggedIn }) {

  const { pathname } = useLocation()
  console.log('location:', location)

  return (
    <header className="header">
        <a className="header__logo" href="#"></a>
        {pathname==='/sign-up' && <button onClick={() => onLogout()} className="header__title">Войти</button>}
        {pathname==='/sign-in' && <button onClick={() => onLogout()} className="header__title">Зарегистрироваться</button>}
    </header>  );
}

export default Header;
