import closeButton from '../images/close-button.svg';

import image from '../images/register-succes.svg';

function InfoTooltip({ isLoginFailPopupOpen, isLoginSuccessPopupOpen, onClose }) {
  
    return (
        <article
        className={`popup ${isLoginSuccessPopupOpen ? "popup_opened" : ""}`}>
        <div className="popup__container tooltip__container">
            <button className="popup__close-button" type="button" onClick = {onClose}>
                <img className="popup__image-close" src={closeButton} alt="закрыть попап"/>
            </button>
            <img className="tooltip__image" src={image} />
            
            {isLoginSuccessPopupOpen==='/sign-up' && (<Link to="/sign-in" className="header__link">Войти</Link>)}

            {/* {isLoginSuccessPopupOpen && (<h2 className="tooltip__title">Вы успешно зарегистрировались!</h2>)} */}
            {/* {isLoginFailPopupOpen===true && (<h2 className="tooltip__title">Что-то пошло не так! Попробуйте ещё раз.</h2>)} */}
        </div>
    </article>
)}

export default InfoTooltip;
