import '../index.css';
import closeButton from '../images/close-button.svg';


function ImagePopup({card, onClose}) {
  return (
    <>
      <article className="popup popup_zoom">
        <div className="popup__zoom-container popup__container">
          <button className="popup__close-button" type="button"
            onClick = {onClose}>
            <img className="popup__image-close" src={closeButton} alt="закрыть попап"></img>
          </button>
          <figure className="popup__figure">
            <img className="popup__image" src={`${isOpen ? card.id : ""}`} alt=""
            onClick = {onCardClick}
            ></img>
            <figcaption className="popup__caption"></figcaption>
          </figure>
        </div>
      </article>
    </>
)}

export default ImagePopup;
